import React, { Component } from 'react';
import { Dropdown, Option } from "./Dropdown";

class Card extends Component {

    constructor(props) {
        super(props);
        this.state = {
        updatedCardPrice: this.props.cardPrice
    }
}


    handlePriceChange = (event) => {
        console.log("hi")
        let newPrice = "0";
        if (event.target.value === "Vanilla milk") {
            newPrice = ".5"
        }
        else if (event.target.value === "Double chocolate") {
            newPrice = "1.5"
        }
        newPrice = +newPrice + +this.props.cardPrice;
        this.setState({ cardPrice: newPrice }, () => {
            console.log(this.state.cardPrice);
          }); 
      };

    render() {
        return (
            <div>
                <img className="card-image" src={this.props.cardImgURL} alt={this.props.cardAlt} />

                <div className="card-title">
                    <h2>{this.props.cardTitle}</h2>
                </div>

                <div className="card-glazing">
                    <label htmlFor="glazing1">Glazing:</label>
                    <Dropdown
                        action="/"
                        onChange={this.handlePriceChange}
                    >
                        <Option textContent="Keep original" value="0"/>
                        <Option textContent="Sugar milk" value="0"/>
                        <Option textContent="Vanilla milk" value=".5"/>
                        <Option textContent="Double chocolate" value="1.5"/>
                    </Dropdown>
                </div>

                <div className="card-size">
                    <div className="card-size-title">
                        <p>Pack size: </p>
                    </div>
                    <div className="card-size-squares">
                        <button type="button" className="button-1 selected">1</button>
                        <button type="button" className="button-3">3</button>
                        <button type="button" className="button-6">6</button>
                        <button type="button" className="button-12">12</button>
                    </div>
                </div>

                <div className="card-price">
                    <div className="card-price-left">
                        <p>{this.state.updatedCardPrice}</p>
                    </div>
                    <div className="card-price-right">
                        <button type="button">Add to Cart</button>
                    </div>
                </div>
            </div>
        )
    }
}

export default Card
