import React, { Component } from 'react';
import { Popup } from './Popup';

class Navbar extends Component {

    constructor(props) {
        super(props);
        this.state = {
            stringItems: "item",
            showCart: false,
            hide: true
        }
    }

    pluralize() {
        if (this.props.items != 1) {
            this.state.stringItems = "items"
        }
        else {
            this.state.stringItems = "item"
        }
    }

    displayCart() {
        if (this.props.items >= 0) {
            this.state.showCart = true
        }
    }

    displayPopup() {
        if (this.props.display) {
            this.state.hide =  false
            this.popUp()
        }
    }

    popUp () {
        setTimeout(() => {
            this.state.hide = true
          }, 3000);
    }




    render() {
        return (
            <div>
                <div className="navbar">

                    <div className="navbar-left">
                        <img className="navbar-img" src="assets/logo-01.svg" alt="bun bun bake shop cinnamon roll logo" />
                    </div>

                    <div className="navbar-right">

                        <div className="navbar-links">
                            <a href="" id="selected-link">PRODUCTS</a>
                            <a href="">CART</a>
                        </div>

                        <div className="cart-items" style={{ display: this.state.showCart ? "block" : "none" }}>
                            {this.pluralize()}
                            {this.displayCart()}
                            <p>{this.props.items} {this.state.stringItems}</p>
                            <p>Total: $ {this.props.price}</p>
                        </div>

                        {!this.state.hide && <div class="cart-popup">
                            <p>Added to cart: </p>
                            <p>{this.props.itemName}</p>
                            <p>{this.props.itemGlazing} glazing</p>
                            <p>Pack of {this.props.itemPackSize}</p>
                            <p>Price: ${this.props.itemPrice}</p>
                        </div>}

                        <hr className="navbar-divider" />

                        <div className="navbar-title">
                            <h1>Our hand-made cinnamon rolls</h1>
                        </div>

                    </div>
                </div>
            </div>
        )
    }
}

export default Navbar
